'    s VARCHAR2(32767); ',
'    je JSON_ELEMENT_T;',
'    jo JSON_OBJECT_T;',
'    res clob;',
'begin',
'  je := JSON_ELEMENT_T.parse(''{',
'    "idleTimeoutInSeconds": 3600',
'}'');  ',
'  jo := treat(je AS JSON_OBJECT_T);',
