'    "userMessage": "Your question",',
'    "sessionId": "xxxx",',
'    "shouldStream": false',
'  }'');  ',
'  jo := treat(je AS JSON_OBJECT_T);',
'  jo.put(''userMessage'', message);',
'  jo.put(''sessionId'', agent_session_id);',
'',
'  -- RAG filter',
'  --   "toolParameters": {',
'  --     "rag": "{"filterConditions\":[{\"field\":\"type\",\"field_type\":\"string\",\"operation\":\"contains\",\"value\":\"Music\"}]}"',
'  --   }',
'  log( ''filter_folder'', filter_folder ); ',
'  if filter_folder='''' then',
