'  elsif filter_folder is not null then',
'    declare',
'      je2 JSON_ELEMENT_T;',
'      jo2 JSON_OBJECT_T;',
'      aFilterConditions JSON_ARRAY_T;',
'      eFilterCondition JSON_ELEMENT_T;',
'      oFilterCondition JSON_OBJECT_T;         ',
'      jo3 JSON_OBJECT_T;',
'      rag_filter varchar2(1024);',
'    begin',
'      je2 := JSON_ELEMENT_T.parse(''{"filterConditions":[{"field":"folder","field_type":"string","operation":"contains","value":"xxxx"}]}'');',
'      jo2 := treat(je2 AS JSON_OBJECT_T);',
'      log( ''jo2'', jo2.to_string );  ',
'      aFilterConditions := jo2.get_Array(''filterConditions'');  ',
'      log( ''aFilterConditions'', aFilterConditions.to_string );    ',
'      eFilterCondition := aFilterConditions.get(0);    ',
'      oFilterCondition := TREAT( eFilterCondition AS JSON_OBJECT_T);',
'      oFilterCondition.put(''value'', filter_folder);     ',
'      rag_filter := je2.to_string;',
'      log( ''rag_filter'', rag_filter );    ',
'      jo3 := json_object_t.parse (''{"rag":"xxx"}'');  ',
'      jo3.put( ''rag'', rag_filter );',
'      jo.put( ''toolParameters'', jo3);',
'    end;',
'  end if;',
'',
'  log( ''<ai_agent_execute> request'', jo.to_clob());',
'  log( ''<ai_agent_execute> LENGTH'', ''LENGTH='' || LENGTH(jo.to_clob()));',
'  resp := DBMS_CLOUD.send_request( ',
'        credential_name => ''OCI$RESOURCE_PRINCIPAL'', ',
'        uri =>''https://agent-runtime.generativeai.'' || g_region || ''.oci.oraclecloud.com/20240531/agentEndpoints/''||agent_endpoint_id||''/actions/chat'',',
'        method => ''POST'', ',
'        body => APEX_UTIL.CLOB_TO_BLOB(jo.to_clob)                                                                                         ',
'    ); ',
'  /*',
'  resp := DBMS_CLOUD.send_request( ',
