'FUNCTION create_pre_authenticated_request( url varchar2, page number ) return varchar2 is',
'  resp DBMS_CLOUD_TYPES.resp;   ',
'  json_response CLOB; ',
'  s VARCHAR2(32767); ',
'  je JSON_ELEMENT_T;',
'  jo JSON_OBJECT_T;  ',
'',
'  res varchar2(4096);',
'  d varchar2(4096);',
'  url1 varchar2(4096);',
'  url2 varchar2(4096);',
'  pos_sep number;',
'begin',
'  pos_sep := instr( url, ''/o/'' );',
'  url1 := substr( url, 1, pos_sep-1 );',
'  url2 := substr( url, pos_sep+3 );',
'  dbms_output.put_line( url1 );',
'  dbms_output.put_line( url2 );',
'',
'  je := JSON_ELEMENT_T.parse(''',
'  { "accessType":"ObjectRead",',
'    "name":"par-object-20241118-1126",',
'    "timeExpires":"2024-11-25T10:26:34.017Z",',
'    "objectName":"xxxx.pdf"',
'  }'');  ',
'  d := to_char( systimestamp + 10.0/1440.0, ''YYYY-MM-DD"T"HH24:MI:SS"Z"'');',
'  jo := treat(je AS JSON_OBJECT_T);',
'  jo.put(''name'', ''apex'' || d);',
'  jo.put(''timeExpires'', d);',
'  jo.put(''objectName'', utl_url.unescape( url2, url_charset => ''UTF-8'' ));',
'  log( ''<create_pre_authenticated_request> request'', jo.to_clob());',
'  log( ''<create_pre_authenticated_request> LENGTH'', ''LENGTH='' || LENGTH(jo.to_clob()));',
'  resp := DBMS_CLOUD.send_request( ',
'        credential_name => ''OCI$RESOURCE_PRINCIPAL'', ',
'        uri =>url1  || ''/p/'',',
'        method => ''POST'', ',
'        body => APEX_UTIL.CLOB_TO_BLOB(jo.to_clob)                                                                                         ',
'    ); ',
'  json_response := DBMS_CLOUD.get_response_text(resp); ',
'  log( ''<create_pre_authenticated_request> response'', json_response);',
'  res := JSON_VALUE(json_response, ''$.fullPath'');',
'  if page is not null then',
'    res := res || ''#page='' || page;',
'  end if;',
'  return res;',
'end;',
'',
'-------------------------------------------------------------------------------',
'',
'FUNCTION json2html( input CLOB ) return CLOB is',
'    v_json_array   JSON_ARRAY_T;',
'    v_json_obj     JSON_OBJECT_T;',
'',
'    v_html_output  CLOB := ''<table class="sql">'';',
'    v_keys         DBMS_SQL.VARCHAR2_TABLE;',
'    v_key_index    PLS_INTEGER := 1;',
'    v_temp_key     VARCHAR2(4000);',
'    v_value        VARCHAR2(4000);',
'',
'    v_key_set      PLS_INTEGER := 0;',
'BEGIN',
'    log(''<json2html>input='', input); ',
'',
'    -- Parse the JSON input into a JSON_ARRAY_T',
'    v_json_array := JSON_ARRAY_T.parse(input);',
'',
'    -- Step 1: Collect all unique keys',
'    FOR i IN 0 .. v_json_array.get_size - 1 LOOP',
'        v_json_obj := TREAT(v_json_array.get(i) AS JSON_OBJECT_T);',
'        IF v_json_obj IS NOT NULL THEN',
'            FOR j IN 1 .. v_json_obj.get_keys().COUNT LOOP',
'                v_temp_key := v_json_obj.get_keys()(j);',
'                -- Check for duplicates before adding',
'                v_key_set := 0;',
'                FOR k IN 1 .. v_key_index - 1 LOOP',
'                    IF v_keys(k) = v_temp_key THEN',
'                        v_key_set := 1;',
'                        EXIT;',
'                    END IF;',
'                END LOOP;',
'',
'                IF v_key_set = 0 THEN',
'                    v_keys(v_key_index) := v_temp_key;',
'                    v_key_index := v_key_index + 1;',
'                END IF;',
'            END LOOP;',
'        END IF;',
'    END LOOP;',
'',
'    -- Step 2: Build table header',
'    v_html_output := v_html_output || ''<tr>'';',
'    FOR i IN 1 .. v_key_index - 1 LOOP',
'        v_html_output := v_html_output || ''<th>'' || v_keys(i) || ''</th>'';',
'    END LOOP;',
'    v_html_output := v_html_output || ''</tr>'';',
'',
'    -- Step 3: Build rows',
'    FOR i IN 0 .. v_json_array.get_size - 1 LOOP',
'        v_json_obj := TREAT(v_json_array.get(i) AS JSON_OBJECT_T);',
'        v_html_output := v_html_output || ''<tr>'';',
'',
'        FOR j IN 1 .. v_key_index - 1 LOOP',
'            BEGIN',
'                v_value := v_json_obj.get_string(v_keys(j));',
'            EXCEPTION',
'                WHEN OTHERS THEN',
'                    v_value := ''''; -- In case the key is missing',
'            END;',
'',
'            v_html_output := v_html_output || ''<td>'' || NVL(v_value, '''') || ''</td>'';',
'        END LOOP;',
'',
'        v_html_output := v_html_output || ''</tr>'';',
'    END LOOP;',
'',
'    -- Step 4: Close table',
'    v_html_output := v_html_output || ''</table>'';',
'',
'    -- Result',
'    log(''</json2html>'', v_html_output);     ',
'    return v_html_output;',
'END;',
'',
'-------------------------------------------------------------------------------',
'',
'FUNCTION ADD_TOOL( arguments varchar2 ) return varchar2 is',
'  number1 number;',
'  number2 number;',
'begin ',
'  number1 := TO_NUMBER(JSON_VALUE(arguments, ''$.number1''));',
'  number2 := TO_NUMBER(JSON_VALUE(arguments, ''$.number2''));',
'  return ''ADD: '' || to_char(number1 + number2);',
'exception',
'  when others then',
'    return ''ERROR: add tools. Failed to add the numbers. Arguments='' || arguments;',
'end;',
'',
'-------------------------------------------------------------------------------',
'',
'FUNCTION agent2html( res CLOB, session_id varchar2 ) return CLOB is',
'  message clob;',
'  citation clob := '''';',
'  url clob;',
'  url_page clob;',
'  doc_name varchar2(4096);',
'  je JSON_ELEMENT_T;',
'  jo JSON_OBJECT_T;',
'  ja JSON_ARRAY_T;  ',
'  a_page JSON_ARRAY_T;  ',
'  l_id number;',
'  doc_page number := null;',
